    private void finalizarActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_finalizarActionPerformed
    {//GEN-HEADEREND:event_finalizarActionPerformed
        usuario = new project.Model.Usuario();
        usuario.setNombre(usertxt.getText());
        usuario.setPasswd(passwdtxt.getText());
        
        java.util.ArrayList<project.Model.Usuario> usuarios = project.Controller.Application.loadDbItems(usuario, "nombre", "'" + usuario.getNombre() + "'" );
        if( usuarios != null )
        {   
            if( usuarios.size() > 0 && usuarios.get(0).getPasswd().equals(usuario.getPasswd()))
            {
                project.Controller.Application.onLoginEvent(usuario);
                project.Controller.Application.callInicio();
            }
            else
                project.Controller.Application.showMessageDialog( "Usuario no encontrado", "Verifique la combinaci√≥n Usuario/Contrase√±a", 1, new String[]{"Aceptar"}, new String[]{"accept"} );
        }
        else
            project.Controller.Application.showSqlErrorMessageDialog();
        
        project.Controller.Application.repaintScreen();
    }//GEN-LAST:event_finalizarActionPerformed


